def maximum_path_recursive(big_list,i,j,levels):
    
    if (j == len(big_list[len(big_list)-1])): # If we are at the last column
        
        return 0
    
    if (i == levels - 2): # If we are at the penultimate level of the pyramid
        

        return big_list[i][j]
    
       
        
    return big_list[i][j] + max(maximum_path_recursive(big_list, i+1, j, levels),maximum_path_recursive(big_list, i+1, j+1, levels))
    

